<?php

namespace Classes\Project;

use MaxBrennemann\PhpUtilities\Tools;

class Icon
{

    public static function ajaxIcon()
    {
        $name = Tools::get("name");
    }

    /**
     * returns an svg icon with the given parameters
     * @param string $icon
     * @param int $width
     * @param int $height
     * @param array $class
     * @param string $title
     * 
     * @return string
     */
    public static function get($icon, $width, $height, $class = [], $title = ""): string
    {
        $classes = implode(" ", $class);
        $size = "width:{$width}px;height:{$height}px";

        $icon = Icon::${$icon};
        return "<svg class=\"{$classes}\" style=\"{$size}\" viewBox=\"0 0 24 24\" title=\"{$title}\">{$icon}</svg>";
    }

    /**
     * returns an svg icon with some default parameters
     * @param string $icon
     * 
     * @return string
     */
    public static function getDefault($icon): string
    {
        return Icon::get($icon, 15, 15, ["inline"]);
    }

    public static function getDefaultColorized($icon, $color): string
    {
        $icon = Icon::get($icon, 15, 15, ["inline"]);
        return str_replace("currentColor", $color, $icon);
    }

    /**
     * returns an svg icon with the given parameters and a color
     * @param string $icon
     * @param int $width
     * @param int $height
     * @param string $color
     * @param array $class
     * @param string $title
     * 
     * @return string
     */
    public static function getColorized($icon, $width, $height, $color, $class = [], $title = ""): string
    {
        $icon = Icon::get($icon, $width, $height, $class, $title);
        return str_replace("currentColor", $color, $icon);
    }

